import React, { useState } from 'react';
import './DashboardCards.css';


const SafetyPassport = () => {
    const [searchBy, setSearchBy] = useState(columnHeaders[0]);
    const [searchText, setSearchText] = useState('');
  
    const handleSearchByChange = (event) => {
      setSearchBy(event.target.value);
    };
  
    const handleSearchTextChange = (event) => {
      setSearchText(event.target.value);
    };    
    const columnHeaders = [
        'Employee ID', 
        'Full Name', 
        'Company Name', 
        'Valid From', 
        'Valid Till', 
        'Employee Permit', 
        'Safety Training'
      ];
      

  return (
    <div className="safetypassport-wrapper">
        <h1 className="Safety-Passport-title">Safety Passport</h1>
        <select value={searchBy} onChange={handleSearchByChange} className="search-by-dropdown">
            {columnHeaders.map((header, index) => (
            <option key={index} value={header}>
                {header}
            </option>
            ))}
        </select>
        <input type="text" placeholder="Enter text here" value={searchText} onChange={handleSearchTextChange} className="search-input"/>
    </div>
  );
};

export default SafetyPassport;
