import React, { useState } from 'react';
import './Safetypassport.css'; // Make sure you link to the correct CSS file

const SafetyPassport = ({ columnHeaders }) => {
  const [searchBy, setSearchBy] = useState(columnHeaders[0]);
  const [searchText, setSearchText] = useState('');

  const handleSearchByChange = (event) => {
    setSearchBy(event.target.value);
  };

  const handleSearchTextChange = (event) => {
    setSearchText(event.target.value);
  };

  return (
    <div className="search-bar">
    <h1>Safety Passport</h1>
      <select 
        value={searchBy} 
        onChange={handleSearchByChange} 
        className="search-by-dropdown"
      >
        {columnHeaders.map((header, index) => (
          <option key={index} value={header}>
            {header}
          </option>
        ))}
      </select>
    </div>
  );
};

export default SafetyPassport;
