import React, { useState } from 'react';
import './Safetypassport.css'; // Assume you have a corresponding CSS file

// Assuming these are your table column headers

const SafetyPassport = () => {
    const [searchBy, setSearchBy] = useState(columnHeaders[0] || '');
    const [searchText, setSearchText] = useState('');
  
    // This handler will be called when the user selects a different search criteria
    const handleSearchByChange = (event) => {
      setSearchBy(event.target.value);
    };
  
    // This handler will be called when the user types into the search text input
    const handleSearchTextChange = (event) => {
      setSearchText(event.target.value);
    };

    const columnHeaders = ['Employee ID', 'Full Name', 'Company Name', 'Valid From', 'Valid Till', 'Employee Permit', 'Safety Training'];

    return (
      <div className="search-by-container">
        <select value={searchBy} onChange={handleSearchByChange} className="search-by-dropdown">
          {columnHeaders.map((header, index) => (
            <option key={index} value={header}>
              {header}
            </option>
          ))}
        </select>
        <input 
          type="text" 
          placeholder="Enter text here" 
          value={searchText} 
          onChange={handleSearchTextChange} 
          className="search-input" 
        />
      </div>
    );
  };

export default SafetyPassport;
