import React, { useEffect, useState } from 'react';
import './Safetypassport.css'; // Make sure you link to the correct CSS file
import downarrow from './Images/select_arrow.png';

const SafetyPassport = () => {

  const columnHeaders = [
    { name: 'Employee ID', type: 'number' },
    { name: 'Full Name', type: 'text' },
    { name: 'Company Name', type: 'text' },
    { name: 'Valid From', type: 'date' },
    { name: 'Valid Till', type: 'date' },
    { name: 'Employee Permit', type: 'text' },
    { name: 'Safety Training', type: 'text' }
  ];
  
      
  const [searchBy, setSearchBy] = useState('');
  const [searchText, setSearchText] = useState('');
  const [searchType, setSearchType] = useState('text');
  const [error, setError] = useState('');
  const [isToggleActive, setIsToggleActive] = useState(true);
  const [isSecondToggleActive, setIsSecondToggleActive] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [tableData, setTableData] = useState([]);
  const recordsPerPage = 11;

  const handleSearchByChange = (event) => {
    const selectedHeader = columnHeaders.find(header => header.name === event.target.value);
    setSearchBy(event.target.value);
    setSearchType(selectedHeader ? selectedHeader.type : 'text');
    setSearchText(''); 
    setError('');
  };

  const handleSearchTextChange = (event) => {
    const value = event.target.value;
    setSearchText(value);

    let validInput = true;
    if (searchType === 'number' && isNaN(value)) {
      validInput = false;
    } else if (searchType === 'date') {
      const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
      validInput = dateRegex.test(value);
    }

    if (!validInput) {
      setError(`Please enter a valid ${searchType}`);
    } else {
      setError(''); 
    }

    if (validInput) {
      
    }
  };
  const handleToggleChange = () => {
    setIsToggleActive(!isToggleActive);
  };
  const handleSecondToggleChange = () => {
    setIsSecondToggleActive(!isSecondToggleActive);
  };
  const resetSearch =() => {
    setSearchBy('');
    setSearchText('');
  };
  const handleKeyPress = (event) =>{
    if (event.key === 'Enter') {
      resetSearch();
    }
  };
  const indexOfLastRecord = currentPage * recordsPerPage;
  const indexOfFirstRecord = indexOfLastRecord - recordsPerPage;
  const currentRecords = tableData.slice(indexOfFirstRecord, indexOfLastRecord);
  const totalPages = Math.ceil(currentRecords.length / recordsPerPage);
  const goToPreviousPage = () => setCurrentPage(page => Math.min(page + 1, totalPages));
  const goToNextPage = () => setCurrentPage(page => Math.max(page - 1, 1));

  useEffect(() => {
    fetch('/api/safety-passport')
        .then(response => {
            if (!response.ok) {
                throw new Error(`HTTP errror! status : ${response.status}`);
            }
            return response.json();
        })
        .then(data => {
            if (data.success) {
                setTableData(data.data);
            } else {
                throw new Error(data.message);
            }
        })
}, []);

  return (
    <div className='Safetypassport-wrapper'>
        <div className='Safetypassport-header'>
          <h1 className='Safetypassport-title'>Safety Passport</h1>
          <button className="export-button">EXPORT</button>
        </div> 
        <div className='Safetypassport-search-wrapper'>
              <select 
                  value={searchBy} 
                  onChange={handleSearchByChange} 
                  className="search-by-dropdown"
                  onKeyDown={handleKeyPress}
              >   <option value="" disabled hidden>Search By</option>
                  {columnHeaders.map((header, index) => (
                  <option key={index} value={header}>
                      {header}
                  </option>
                  ))}
                  <img src={downarrow} alt='down-arrow' className='downarrow'/>
              </select>
              <input 
                  type="text" 
                  placeholder="Enter text here" 
                  value={searchText} 
                  onChange={handleSearchTextChange} 
                  className="search-input"
              />
            <span className="toggle-label">Active</span>
            <div className="toggles-wrapper">
              <div className="toggle-container">
                <label className="switch">
                  <input type="checkbox" checked={isToggleActive} onChange={handleToggleChange} />
                  <span className="slider round"></span>
                </label>
              </div>
              <div className="toggle-container">
                <label className="switch">
                  <input type="checkbox" checked={isSecondToggleActive} onChange={handleSecondToggleChange} />
                  <span className="slider round"></span>
                </label>
              </div>
            </div>
        </div>
        <div className='table-controls'>
                <div className='checkbox-wrapper'>
                  <label className='checkbox-container'>
                    <input type="checkbox" />
                    <span className='checkmark'></span>
                  </label>
                  <img src={downarrow} className='arrow-down'></img>
                </div>
                <div className='pagination'>
                  <button onClick={goToPreviousPage} disabled={currentPage === 1}>&lt;</button>
                  <span className='current'>{currentPage}</span>
                  <button onClick={goToNextPage} disabled={currentPage === totalPages}>&gt;</button>
                  <span>of {totalPages}</span>
                </div>
        </div>
        <div className='data-table-container'>
          <table className='data-table'>
              <thead>
                <tr>
                <th></th>
                  {columnHeaders.map((header, index) => (
                    <th key={index}>{header}</th>
                  ))}
                </tr>
              </thead>
            <tbody>
              {currentRecords.map((data, index) => (
                <tr key={index} className={index % 2 === 0 ? 'even-row':'odd-row'}> 
                <td>
                  <label className='row-checkbox-container'>
                    <input type="checkbox" />
                  </label>
                </td>
                  <td>{data.EmployeeID}</td>
                  <td>{data.FullName}</td>
                  <td>{data.CompanyName}</td>
                  <td>{data.ValidFrom}</td>
                  <td>{data.ValidTill}</td>
                  <td>{data.EmployeePermit}</td>
                  <td>{data.SafetyTraining}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div className='footer-wrapper'>
          <button className="next-button" onClick={goToNextPage}>NEXT</button>
          <div className='pagination'>
            <button onClick={goToPreviousPage} disabled={currentPage === 1}>&lt;</button>
            <span className='current'>{currentPage}</span>
            <button onClick={goToNextPage} disabled={currentPage === totalPages}>&gt;</button>
            <span>of {totalPages}</span>
          </div>
        </div>
    </div>
  );
};

export default SafetyPassport;
